import'./login.css'
import  logo from '../NavBar/my_logo.jpg'
import userAddon from './user.png'
import passwordAddon from './password.png'
import axios from 'axios'
import { useState } from 'react'
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom'

function LogIn () {

  const history = useNavigate()
  const [userName, setUserName] = useState('')
  const [password, setPassword] = useState('')

   
  const handleSubmit = async (e) => {
     e.preventDefault()

     try {
      await axios.post('/api/v1/auth/login', {
        userName, password
      }) 
      .then(res => {
        if(res.data = 'exists') {
            history('/MainPage',{state : {id : userName}})
        }

        else if (res.data = 'not exist') {
          alert
        }
      })
     }

     catch (e) {
      console.log(e)
     }

  }

 
    return(
        <>

      <img className="col" src={logo} width="350px" />

      <form action='POST'  className='form-control ' style={{background: 'whitesmoke', border : 'none'}} >

      <div className="input-group mb-3 space-top w-25 mx-auto">
        <span className="input-group-text" id="basic-addon1">
          <img src={userAddon} width='30px' height='30px' style={{border: 'none'}} />
        </span>
        <input
          type="text"
          className="form-control"
          placeholder="Username"
          name="userName"
          aria-label="Username"
          aria-describedby="basic-addon1"
          onChange={(e) => {
            e.preventDefault()
            setUserName(e.target.value)
          }}
        />
      </div>
      <div className=" input-group col-sm-3 mb-3 w-25 space-left">
      <span className="input-group-text" id="basic-addon1">
          <img src={passwordAddon} width='30px' height='30px' style={{border: 'none'}} />
        </span>

        <input
          type="password"
          className="form-control"
          name="password"
          id="inputPassword"
          placeholder="password"
          onChange={(e) => {
            e.preventDefault()
            setPassword(e.target.value)
          }}
          
        />
      </div>
     <div id= 'btn' className="col-md-12 text-center">
     <Link to='/MainPage'><button className="btn btn-primary" onChange={handleSubmit}>Log In</button></Link>
     </div>

     <div id= 'btn' className="col-md-12 text-center">
     <Link to='/Register'> <button className="btn btn-primary" >Register</button> </Link>
     </div>
   </form>
     
</>
    )

}

export default LogIn
